import React from "react";
import "../Css/RadioComponent.css";

const RadioComponent = ({ text, changeSelection, data, image }) => {
  // Changing selected data from the user.
  const handleChecked = (value) => {
    changeSelection(value);
  };

  return (
    <div
      name={text}
      className={`form-check-label ${data === text ? "active" : "inactive"}`}
      onClick={() => handleChecked(text)}
    >
      {/* Image inside button */}
      {image && <img src={image} alt={text} className="radio-image" />}
      <span className="text">{text}</span>
    </div>
  );
};

export default RadioComponent;
